    async Task LoadPecas()
    {
        pecas = await http.GetFromJsonAsync<List<Peca>>("api/peca");
    }

    async Task ExcluirPeca(int ID)
    {
        await http.DeleteAsync($"api/peca/{ID}");
        await LoadPecas();

